SET NAMES utf8mb4;
SET time_zone = '+00:00';

DROP TABLE IF EXISTS pdf_images;
DROP TABLE IF EXISTS pdf_pages;
DROP TABLE IF EXISTS pdf_metadata;
DROP TABLE IF EXISTS pdf_documents;

CREATE TABLE pdf_documents (
  id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT,
  source_path TEXT NULL,
  filename VARCHAR(255) NULL,
  bytes BIGINT UNSIGNED NULL,
  extracted_at DATETIME NULL,
  page_count INT UNSIGNED NULL,
  total_char_count INT UNSIGNED NULL,
  total_word_count INT UNSIGNED NULL,
  image_count INT UNSIGNED NULL,
  full_text LONGTEXT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE pdf_metadata (
  id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT,
  document_id BIGINT UNSIGNED NOT NULL,
  meta_key VARCHAR(255) NOT NULL,
  meta_value LONGTEXT NULL,
  PRIMARY KEY (id),
  KEY idx_pdf_metadata_document_id (document_id),
  CONSTRAINT fk_pdf_metadata_document FOREIGN KEY (document_id) REFERENCES pdf_documents(id) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE pdf_pages (
  id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT,
  document_id BIGINT UNSIGNED NOT NULL,
  page_number INT UNSIGNED NOT NULL,
  text LONGTEXT NULL,
  char_count INT UNSIGNED NULL,
  word_count INT UNSIGNED NULL,
  PRIMARY KEY (id),
  UNIQUE KEY uq_pdf_pages_document_page (document_id, page_number),
  KEY idx_pdf_pages_document_id (document_id),
  CONSTRAINT fk_pdf_pages_document FOREIGN KEY (document_id) REFERENCES pdf_documents(id) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE pdf_images (
  id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT,
  document_id BIGINT UNSIGNED NOT NULL,
  page_number INT UNSIGNED NOT NULL,
  image_index INT UNSIGNED NOT NULL,
  name VARCHAR(255) NULL,
  file_path TEXT NULL,
  bytes INT UNSIGNED NULL,
  PRIMARY KEY (id),
  UNIQUE KEY uq_pdf_images_document_page_index (document_id, page_number, image_index),
  KEY idx_pdf_images_document_id (document_id),
  CONSTRAINT fk_pdf_images_document FOREIGN KEY (document_id) REFERENCES pdf_documents(id) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

